// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.25;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./tokens/NalndaToken.sol";
import "./NalndaMarketplace.sol";

contract NalndaAirdrop is Ownable {
    enum AirdropSlab {
        ZeroToFiveK,
        FiveK1ToTenK,
        TenK1ToTwentyK,
        TwentyK1ToThirtyK,
        ThirtyK1ToFiftyK,
        MoreThanFiftyK
    }

    NalndaToken public immutable nalndaToken;
    uint256 public immutable maxAirdropTokens;
    NalndaMarketplace immutable marketplace;

    uint256 private ctrForAirdrop;
    AirdropSlab public currentSlab;

    modifier onlyMarketplace() {
        require(msg.sender == address(marketplace), "Only the marketplace can call this function");
        _;
    }

    mapping(AirdropSlab => uint256) public tokensToDistributePerBook;

    constructor(address _initOwner, address _marketplace) Ownable(_initOwner) {
        nalndaToken = new NalndaToken(address(this), _initOwner);
        maxAirdropTokens = 13500000 * 10 ** 18; // 13.5M
        require(nalndaToken.balanceOf(address(this)) == maxAirdropTokens, "Invalid airdrop token balance");
        marketplace = NalndaMarketplace(_marketplace);

        tokensToDistributePerBook[AirdropSlab.ZeroToFiveK] = 1000 * 10 ** nalndaToken.decimals();
        tokensToDistributePerBook[AirdropSlab.FiveK1ToTenK] = 500 * 10 ** nalndaToken.decimals();
        tokensToDistributePerBook[AirdropSlab.TenK1ToTwentyK] = 250 * 10 ** nalndaToken.decimals();
        tokensToDistributePerBook[AirdropSlab.TwentyK1ToThirtyK] = 150 * 10 ** nalndaToken.decimals();
        tokensToDistributePerBook[AirdropSlab.ThirtyK1ToFiftyK] = 100 * 10 ** nalndaToken.decimals();

        ctrForAirdrop = 0;
    }

    modifier calledByBook(address book) {
        require(marketplace.createdBooks(book), "Only books created by the marketplace allowed!");
        _;
    }

    function currentNalndaBalance() public view returns (uint256) {
        return nalndaToken.balanceOf(address(this));
    }

    function distributeTokensIfAny(address buyer) public calledByBook(msg.sender) {
        if (isAirdropActive() == false) {
            return;
        }
        adjustSlabIfNeeded();
        if (currentSlab == AirdropSlab.MoreThanFiftyK) {
            return;
        }
        uint256 nalndaBalance = nalndaToken.balanceOf(address(this));
        if (nalndaBalance < tokensToDistributePerBook[currentSlab]) {
            return;
        }
        nalndaToken.transfer(buyer, tokensToDistributePerBook[currentSlab]);
    }

    function adjustSlabIfNeeded() private {
        if (currentSlab == AirdropSlab.MoreThanFiftyK) {
            return;
        }
        ++ctrForAirdrop;
        if (ctrForAirdrop > 0 && ctrForAirdrop <= 5000) {
            if (currentSlab != AirdropSlab.ZeroToFiveK) {
                currentSlab = AirdropSlab.ZeroToFiveK;
            }
        } else if (ctrForAirdrop > 5000 && ctrForAirdrop <= 10000) {
            if (currentSlab != AirdropSlab.FiveK1ToTenK) {
                currentSlab = AirdropSlab.FiveK1ToTenK;
            }
        } else if (ctrForAirdrop > 10000 && ctrForAirdrop <= 20000) {
            if (currentSlab != AirdropSlab.TenK1ToTwentyK) {
                currentSlab = AirdropSlab.TenK1ToTwentyK;
            }
        } else if (ctrForAirdrop > 20000 && ctrForAirdrop <= 30000) {
            if (currentSlab != AirdropSlab.TwentyK1ToThirtyK) {
                currentSlab = AirdropSlab.TwentyK1ToThirtyK;
            }
        } else if (ctrForAirdrop > 30000 && ctrForAirdrop <= 50000) {
            if (currentSlab != AirdropSlab.ThirtyK1ToFiftyK) {
                currentSlab = AirdropSlab.ThirtyK1ToFiftyK;
            }
        } else {
            if (currentSlab != AirdropSlab.MoreThanFiftyK) {
                currentSlab = AirdropSlab.MoreThanFiftyK;
            }
        }
    }

    function withdrawAllNalndaAndStopAirdrop() external onlyOwner {
        nalndaToken.transfer(owner(), nalndaToken.balanceOf(address(this)));
    }

    function isAirdropActive() public view returns (bool) {
        return nalndaToken.balanceOf(address(this)) > 0;
    }

    function withdrawAnyEther() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }

    receive() external payable {}

    fallback() external payable {}
}

contracts/NalndaBook.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.25;

import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./NalndaMarketplace.sol";
import "./Airdrop.sol";
import "./tokens/NalndaToken.sol";

contract NalndaBook is ERC721, Ownable, Initializable, UUPSUpgradeable {
    uint256 private _nextTokenId;
    NalndaMarketplace public marketplaceContract;
    uint256 public protocolMintFee;
    uint256 public protocolFee;
    uint256 public bookOwnerShare;
    bool public approved;
    uint256 public daysForSecondarySales;
    uint256 public secondarySalesTimestamp;
    uint256 public bookLang;
    uint256[] public bookGenre;
    string public uri;
    uint256 public mintPrice;
    uint256 public authorEarningsPaidout;
    NalndaAirdrop public airdrop;
    NalndaToken public nalndaToken;

    // token id => last sale price
    mapping(uint256 => uint256) public lastSoldPrice;
    //token id => timestamp of last transfer
    mapping(uint256 => uint256) public ownedAt;

    modifier onlyMarketplace() {
        require(_msgSender() == address(marketplaceContract));
        _;
    }

    modifier marketplaceApproved() {
        require(approved == true, "NalndaBook: Book unapproved from marketplace!");
        _;
    }

    constructor(address initialOwner) ERC721("NalndaBookCover", "COVER") Ownable(initialOwner) {
        _disableInitializers();
    }

    function coverIdCounter() public view returns (uint256) {
        return _nextTokenId;
    }

    function initialize(
        address _author,
        string memory _uri,
        uint256 _initialPrice,
        uint256 _daysForSecondarySales,
        uint256 _lang,
        uint256[] memory _genre,
        NalndaAirdrop _airdrop
    ) public virtual initializer {
        require(_author != address(0), "NalndaBook: Author's address can't be null!");
        require(bytes(_uri).length > 0, "NalndaBook: Empty string passed as cover URI!!!");
        require(
            _daysForSecondarySales >= 90 && _daysForSecondarySales <= 150,
            "NalndaBook: Days to secondary sales should be between 90 and 150!"
        );
        require(_lang >= 0 && _lang < 100, "NalndaBook: Book language tag should be between 1 and 100!");
        for (uint256 i = 0; i < _genre.length; i++) {
            require(_genre[i] >= 0 && _genre[i] < 100, "NalndaBook: Book genre tag should be between 1 and 60!");
        }
        airdrop = _airdrop;
        approved = true; // for testing -- should be false in production
        daysForSecondarySales = _daysForSecondarySales;
        secondarySalesTimestamp = 2 ** 256 - 1;
        bookLang = _lang;
        bookGenre = _genre;
        marketplaceContract = NalndaMarketplace(_msgSender());
        _transferOwnership(_author);
        protocolMintFee = 20; //20% on safemint
        protocolFee = 2; //2% on every transfer
        bookOwnerShare = 10; //10% on every transfer
        uri = string(_uri);
        mintPrice = _initialPrice;
        nalndaToken = NalndaToken(marketplaceContract.nalndaToken());
    }

    function changeApproval(bool _newApproved) external onlyMarketplace {
        if (_newApproved == true) {
            require(approved == false, "NalndaBook: Already approved!");
            secondarySalesTimestamp = block.timestamp + daysForSecondarySales * 1 days;
        } else {
            require(approved == true, "NalndaBook: Already unapproved!");
            secondarySalesTimestamp = 2 ** 256 - 1;
        }
        approved = _newApproved;
    }

    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(tokenId > 0 && tokenId <= _nextTokenId, "NalndaBook: URI query for nonexistent token");
        return uri;
    }

    function changeMintPrice(uint256 _newPrice) external onlyOwner {
        mintPrice = _newPrice;
    }

    //owner should be able to mint for free at any point
    function ownerMint(address to) external onlyOwner marketplaceApproved {
        _nextTokenId++;
        uint256 tokenId = _nextTokenId;
        ownedAt[tokenId] = block.timestamp;
        if (to != owner()) {
            //first mint for author then transfer
            _safeMint(owner(), tokenId);
            _transfer(owner(), to, tokenId);
        } else {
            _safeMint(owner(), tokenId);
        }
    }

    function batchOwnerMint(address[] memory addresses) external onlyOwner marketplaceApproved {
        for (uint256 i = 0; i < addresses.length; i++) {
            _nextTokenId++;
            uint256 tokenId = _nextTokenId;
            ownedAt[tokenId] = block.timestamp;
            if (addresses[i] != owner()) {
                //first mint for author then transfer
                _safeMint(owner(), tokenId);
                _transfer(owner(), addresses[i], tokenId);
            } else {
                _safeMint(owner(), tokenId);
            }
        }
    }

    //public method for minting new cover
    function safeMint(address to) external marketplaceApproved {
        IERC20 purchaseToken = IERC20(marketplaceContract.purchaseToken());
        //transfer the minting cost to the contract
        purchaseToken.transferFrom(msg.sender, address(this), mintPrice);
        uint256 protocolPayout = (mintPrice * protocolMintFee) / 100;
        uint256 ownerShare = mintPrice - protocolPayout;
        //send commision to marketplaceContract
        purchaseToken.transfer(address(marketplaceContract), protocolPayout);
        //send author's share to the book owner
        purchaseToken.transfer(owner(), ownerShare);
        authorEarningsPaidout += ownerShare;
        _nextTokenId++;
        uint256 _tokenId = _nextTokenId;
        lastSoldPrice[_tokenId] = mintPrice;
        ownedAt[_tokenId] = block.timestamp;
        //first mint for author then transfer to buyer
        _safeMint(owner(), _tokenId);
        _transfer(owner(), to, _tokenId);
        airdrop.distributeTokensIfAny(to);
    }

    function batchSafeMint(address[] memory addresses) external marketplaceApproved {
        IERC20 purchaseToken = IERC20(marketplaceContract.purchaseToken());
        //transfer the minting cost to the contract
        uint256 cost = mintPrice * addresses.length;
        purchaseToken.transferFrom(_msgSender(), address(this), cost);
        uint256 protocolPayout = (cost * protocolMintFee) / 100;
        uint256 ownerShare = cost - protocolPayout;
        //send commision to marketplaceContract
        purchaseToken.transfer(address(marketplaceContract), protocolPayout);
        //send author's share to the book owner
        purchaseToken.transfer(owner(), ownerShare);
        authorEarningsPaidout += ownerShare;
        for (uint256 i = 0; i < addresses.length; i++) {
            _nextTokenId++;
            uint256 _tokenId = _nextTokenId;
            lastSoldPrice[_tokenId] = mintPrice;
            ownedAt[_tokenId] = block.timestamp;
            //first mint for author then transfer to buyer
            _safeMint(owner(), _tokenId);
            _transfer(owner(), addresses[i], _tokenId);
            airdrop.distributeTokensIfAny(addresses[i]);
        }
    }

    function _chargeTransferFees(uint256 tokenId) internal {
        IERC20 purchaseToken = IERC20(marketplaceContract.purchaseToken());
        uint256 lastSellPrice = lastSoldPrice[tokenId];
        //charging transfer fee
        uint256 totalFee = (lastSellPrice * (bookOwnerShare + protocolFee)) / 100;
        purchaseToken.transferFrom(_msgSender(), address(this), totalFee);
        //send owner share to the book owner
        uint256 ownerShare = (lastSellPrice * bookOwnerShare) / 100;
        purchaseToken.transfer(owner(), ownerShare);
        //send protocol its share
        uint256 protocolShare = (lastSellPrice * protocolFee) / 100;
        purchaseToken.transfer(address(marketplaceContract), protocolShare);
    }

    function safeTransferFrom(address from, address to, uint256 tokenId, bytes memory data)
        public
        virtual
        override
        marketplaceApproved
    {
        require(
            block.timestamp >= ownedAt[tokenId] + marketplaceContract.transferAfterDays() * 1 days,
            "NalndaBook: Transfer not allowed!"
        );
        _chargeTransferFees(tokenId);
        ownedAt[tokenId] = block.timestamp;
        super.safeTransferFrom(from, to, tokenId, data);
    }

    function transferFrom(address from, address to, uint256 tokenId) public virtual override marketplaceApproved {
        require(
            block.timestamp >= ownedAt[tokenId] + marketplaceContract.transferAfterDays() * 1 days,
            "NalndaBook: Transfer not allowed!"
        );
        _chargeTransferFees(tokenId);
        ownedAt[tokenId] = block.timestamp;
        super.transferFrom(from, to, tokenId);
    }

    function marketplaceTransfer(address _from, address _to, uint256 _tokenId) external onlyMarketplace {
        ownedAt[_tokenId] = block.timestamp;
        _transfer(_from, _to, _tokenId);
    }

    function updateLastSoldPrice(uint256 _tokenId, uint256 _price) external onlyMarketplace {
        lastSoldPrice[_tokenId] = _price;
    }

    function _authorizeUpgrade(address newImplementation) internal view override onlyOwner {
        (newImplementation);
    }

    function renounceOwnership() public virtual override onlyOwner {
        revert("NalndaBook: Ownership of a book cannot be renounced!");
    }
}

contracts/NalndaMarketplace.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.25;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";
import "./NalndaBook.sol";
import "./tokens/NalndaToken.sol";
import "@openzeppelin/contracts/utils/Create2.sol";

contract NalndaMarketplace is Ownable {
    IERC20 public purchaseToken;

    NalndaToken public immutable nalndaToken;

    mapping(address => address[]) public authorToBooks;

    uint256 public totalBooksCreated;

    uint256 public lastOrderId;

    uint256 public transferAfterDays;

    uint256 public secondarySaleAfterDays;

    NalndaAirdrop public nalndaAirdrop;

    bool public publicBookCreationAllowed;

    enum Stage {
        UNLISTED,
        LISTED,
        SOLD,
        UNLISTED_BY_ADMIN
    }

    struct Order {
        Stage stage;
        uint256 orderId;
        address seller;
        NalndaBook book;
        uint256 tokenId;
        uint256 price;
    }

    mapping(uint256 => Order) public ORDER;

    mapping(address => bool) public createdBooks;

    event NewBookCreated(
        address indexed _author, address _bookAddress, string _coverURI, uint256 _price, uint256 _lang, uint256[] _genre
    );
    event CoverListed(
        uint256 indexed _orderId, address _lister, address indexed _book, uint256 indexed _tokenId, uint256 _price
    );
    event CoverUnlisted(
        uint256 indexed _orderId, address indexed _book, uint256 indexed _tokenId, Stage _unlistedStage
    );
    event CoverBought(
        uint256 indexed _orderId, address indexed _book, uint256 indexed _tokenId, address _buyer, uint256 _price
    );
    event RevenueWithdrawn(uint256 _revenueWithdrawn);

    NalndaBook public immutable book_implementation;
    uint256 public immutable chainId;
    uint256 private extraSalt;

    address public authorizedBookCreator;

    function setAuthorizedBookCreator(address _newCreator) external onlyOwner {
        authorizedBookCreator = _newCreator;
    }

    function publicCreationAllowed() private view {
        if (publicBookCreationAllowed == true) {
            return;
        }
        require(
            _msgSender() == authorizedBookCreator, "NalndaMarketplace: Only authorized book creator can create books!"
        );
    }

    function allowPublicBookCreation() external onlyOwner {
        publicBookCreationAllowed = true;
    }

    function changePurchaseToken(address _newToken) external onlyOwner {
        require(_newToken != address(0), "NalndaMarketplace: PurchaseToken token's address can't be null!");
        purchaseToken = IERC20(_newToken);
    }

    constructor(address _purchaseToken, address _initOwner, address _authBookCreator) Ownable(_initOwner) {
        require(_purchaseToken != address(0), "NalndaMarketplace: PurchaseToken token's address can't be null!");
        publicBookCreationAllowed = false;
        purchaseToken = IERC20(_purchaseToken);
        transferAfterDays = 21; //21 days
        secondarySaleAfterDays = 21; //user should have owned cover for at least 21 days
        totalBooksCreated = 0;
        lastOrderId = 0;
        extraSalt = 0;
        uint256 _chainid;
        assembly {
            _chainid := chainid()
        }
        chainId = _chainid;
        book_implementation = new NalndaBook(address(this));
        nalndaAirdrop = new NalndaAirdrop(_initOwner, address(this));
        nalndaToken = nalndaAirdrop.nalndaToken();
        authorizedBookCreator = _authBookCreator;
    }

    function changeTransferAfterDays(uint256 _days) external onlyOwner {
        transferAfterDays = _days;
    }

    function changeSecondarySaleAfterDays(uint256 _days) external onlyOwner {
        secondarySaleAfterDays = _days;
    }

    function createNewBook(
        address _author,
        string memory _coverURI,
        uint256 _initialPrice,
        uint256 _daysForSecondarySales,
        uint256 _lang,
        uint256[] memory _genre
    ) external {
        publicCreationAllowed();
        _createNewBook(_author, _coverURI, _initialPrice, _daysForSecondarySales, _lang, _genre);
    }

    function createNewBooks(
        address[] memory _author,
        string[] memory _coverURI,
        uint256[] memory _initialPrice,
        uint256[] memory _daysForSecondarySales,
        uint256[] memory _lang,
        uint256[][] memory _genre
    ) external {
        publicCreationAllowed();
        require(
            _author.length == _coverURI.length && _coverURI.length == _initialPrice.length
                && _initialPrice.length == _daysForSecondarySales.length && _daysForSecondarySales.length == _lang.length
                && _lang.length == _genre.length,
            "NalndaMarketplace: Array lengths should be equal!"
        );
        for (uint256 i = 0; i < _author.length; i++) {
            _createNewBook(_author[i], _coverURI[i], _initialPrice[i], _daysForSecondarySales[i], _lang[i], _genre[i]);
        }
    }

    function _createNewBook(
        address _author,
        string memory _coverURI,
        uint256 _initialPrice,
        uint256 _daysForSecondarySales,
        uint256 _lang,
        uint256[] memory _genre
    ) private {
        require(_author != address(0), "NalndaMarketplace: Author address can't be null!");
        require(bytes(_coverURI).length > 0, "NalndaMarketplace: Empty string passed as cover URI!");
        //require(
        //    _daysForSecondarySales >= 90 && _daysForSecondarySales <= 150,
        //    "NalndaMarketplace: Days to secondary sales should be between 90 and 150!"
        //);
        require(_lang >= 0 && _lang < 100, "NalndaMarketplace: Book language tag should be between 1 and 100!");
        for (uint256 i = 0; i < _genre.length; i++) {
            require(_genre[i] >= 0 && _genre[i] < 100, "NalndaMarketplace: Book genre tag should be between 1 and 60!");
        }
        address _addressOutput =
            _deployBookProxy(_author, _coverURI, _initialPrice, _daysForSecondarySales, _lang, _genre);
        authorToBooks[_msgSender()].push(_addressOutput);
        totalBooksCreated++;
        createdBooks[_addressOutput] = true;
        emit NewBookCreated(_author, _addressOutput, _coverURI, _initialPrice, _lang, _genre);
    }

    function _deployBookProxy(
        address _author,
        string memory _coverURI,
        uint256 _initialPrice,
        uint256 _daysForSecondarySales,
        uint256 _lang,
        uint256[] memory _genre
    ) private returns (address _deployedProxy) {
        extraSalt = extraSalt + 1;
        uint256 salt = uint256(
            keccak256(
                abi.encodePacked(
                    chainId, address(this), _author, _coverURI, _initialPrice, _lang, _genre.length, extraSalt
                )
            )
        );
        _deployedProxy = address(
            NalndaBook(
                payable(
                    new ERC1967Proxy{salt: bytes32(salt)}(
                        address(book_implementation),
                        abi.encodeCall(
                            NalndaBook.initialize,
                            (_author, _coverURI, _initialPrice, _daysForSecondarySales, _lang, _genre, nalndaAirdrop)
                        )
                    )
                )
            )
        );
    }

    function computeNextBookAddress(
        address _author,
        string memory _coverURI,
        uint256 _initialPrice,
        uint256 _daysForSecondarySales,
        uint256 _lang,
        uint256[] memory _genre
    ) public view returns (address _estimatedAddress) {
        _estimatedAddress =
            _computeBookAddress(_author, _coverURI, _initialPrice, _daysForSecondarySales, _lang, _genre, extraSalt + 1);
    }

    function computeNextBooksAddresses(
        address[] memory _author,
        string[] memory _coverURI,
        uint256[] memory _initialPrice,
        uint256[] memory _daysForSecondarySales,
        uint256[] memory _lang,
        uint256[][] memory _genre
    ) public view returns (address[] memory) {
        uint256 _extraSalt = extraSalt;
        address[] memory _estimatedAddresses = new address[](_author.length);
        require(
            _author.length == _coverURI.length && _coverURI.length == _initialPrice.length
                && _initialPrice.length == _daysForSecondarySales.length && _daysForSecondarySales.length == _lang.length
                && _lang.length == _genre.length,
            "NalndaMarketplace: Array lengths should be equal!"
        );
        for (uint256 i = 0; i < _author.length; i++) {
            _estimatedAddresses[i] = _computeBookAddress(
                _author[i],
                _coverURI[i],
                _initialPrice[i],
                _daysForSecondarySales[i],
                _lang[i],
                _genre[i],
                _extraSalt + i + 1
            );
        }
        return _estimatedAddresses;
    }

    function _computeBookAddress(
        address _author,
        string memory _coverURI,
        uint256 _initialPrice,
        uint256 _daysForSecondarySales,
        uint256 _lang,
        uint256[] memory _genre,
        uint256 _extraSalt
    ) private view returns (address _estimatedAddress) {
        uint256 salt = uint256(
            keccak256(
                abi.encodePacked(
                    chainId, address(this), _author, _coverURI, _initialPrice, _lang, _genre.length, _extraSalt
                )
            )
        );

        _estimatedAddress = Create2.computeAddress(
            bytes32(salt),
            keccak256(
                abi.encodePacked(
                    type(ERC1967Proxy).creationCode,
                    abi.encode(
                        address(book_implementation),
                        abi.encodeCall(
                            NalndaBook.initialize,
                            (_author, _coverURI, _initialPrice, _daysForSecondarySales, _lang, _genre, nalndaAirdrop)
                        )
                    )
                )
            )
        );
    }

    function approveBooks(address[] memory _books) public onlyOwner {
        for (uint256 i = 0; i < _books.length; i++) {
            NalndaBook(_books[i]).changeApproval(true);
        }
    }

    function unapproveBooks(address[] memory _books) external onlyOwner {
        for (uint256 i = 0; i < _books.length; i++) {
            NalndaBook(_books[i]).changeApproval(false);
        }
    }

    function bookOwner(address _book) public view returns (address author) {
        author = Ownable(_book).owner();
    }

    function withdrawRevenue() external onlyOwner {
        uint256 balance = getNALNDABalance();
        require(balance != 0, "NalndaMarketplace: Nothing to withdraw!");
        purchaseToken.transfer(owner(), balance);
        emit RevenueWithdrawn(balance);
    }

    function getNALNDABalance() public view returns (uint256 bal) {
        bal = purchaseToken.balanceOf((address(this)));
    }

    function listCover(NalndaBook _book, uint256 _tokenId, uint256 _price) external {
        //require(Address.isContract(address(_book)) == true, "NalndaMarketplace: Invalid book address!");
        require(_tokenId <= _book.coverIdCounter(), "NalndaMarketplace: Invalid tokenId provided!");
        require(_book.ownerOf(_tokenId) == _msgSender(), "NalndaMarketplace: Seller should own the NFT to list!");
        //require(
        //    block.timestamp >= _book.secondarySalesTimestamp(),
        //    "NalndaMarketplace: Listing for this book is disabled!"
        //);
        //require(
        //    block.timestamp >= _book.ownedAt(_tokenId) + secondarySaleAfterDays * 1 days,
        //    "NalndaMarketplace: Can't list the cover at this time!"
        //);
        _book.marketplaceTransfer(_msgSender(), address(this), _tokenId);
        lastOrderId++;
        ORDER[lastOrderId] = Order(Stage.LISTED, lastOrderId, _msgSender(), _book, _tokenId, _price);
        emit CoverListed(lastOrderId, _msgSender(), address(_book), _tokenId, _price);
    }

    function unlistCover(uint256 _orderId) external {
        require(_orderId <= lastOrderId, "NalndaMarketplace: Invalid order id!");
        require(ORDER[_orderId].stage == Stage.LISTED, "NalndaMarketplace: NFT not yet listed / already sold!");
        require(
            _msgSender() == ORDER[_orderId].seller || _msgSender() == owner(),
            "NalndaMarketplace: Only seller or marketplace admin can unlist!"
        );
        _msgSender() == ORDER[_orderId].seller
            ? ORDER[_orderId].stage = Stage.UNLISTED
            : ORDER[_orderId].stage = Stage.UNLISTED_BY_ADMIN;
        //return the seller its cover
        ORDER[_orderId].book.marketplaceTransfer(address(this), ORDER[_orderId].seller, ORDER[_orderId].tokenId);
        emit CoverUnlisted(
            ORDER[_orderId].orderId, address(ORDER[_orderId].book), ORDER[_orderId].tokenId, ORDER[_orderId].stage
        );
    }

    function buyCover(uint256 _orderId) external {
        require(_orderId <= lastOrderId, "NalndaMarketplace: Invalid order id!");
        require(ORDER[_orderId].book.approved() == true, "NalndaMarketplace: Sales on this book are disabled!");
        require(ORDER[_orderId].stage == Stage.LISTED, "NalndaMarketplace: NFT not yet listed / already sold!");
        ORDER[_orderId].stage = Stage.SOLD; //to prevent reentrancy
        purchaseToken.transferFrom(_msgSender(), address(this), ORDER[_orderId].price);
        //send author commision
        uint256 authorShare = (ORDER[_orderId].price * 10) / 100; //10% for author
        purchaseToken.transfer(Ownable(address(ORDER[_orderId].book)).owner(), authorShare);
        //send seller its share
        uint256 sellerShare = (ORDER[_orderId].price * 88) / 100; //88% to the seller
        purchaseToken.transfer(ORDER[_orderId].seller, sellerShare);
        //update last sold price
        ORDER[_orderId].book.updateLastSoldPrice(ORDER[_orderId].tokenId, ORDER[_orderId].price);
        //transfer NFT to the buyer
        ORDER[_orderId].book.marketplaceTransfer(address(this), _msgSender(), ORDER[_orderId].tokenId);
        emit CoverBought(
            ORDER[_orderId].orderId,
            address(ORDER[_orderId].book),
            ORDER[_orderId].tokenId,
            _msgSender(),
            ORDER[_orderId].price
        );
    }
}

